import data from '../../resources/data.json'
import PropTypes from 'prop-types'
import React from 'react'
import { View } from 'react-native'
import styles from './styles'
import BoardList from '../../components/BoardList'

const Main = ({ navigation: { navigate } }) => {
  const [boards, setBoards] = React.useState(data.boards)
  const [images, setImages] = useState([]);
  // All selected images
  const [selectedImages, setSelectedImages] = useState([]);
  // A boolean flag to indicate whether the images are being loaded or not
  const [loadingImages, setLoadingImages] = useState(true);
  const createBoard = () => {
    // Create a new board logic
    const newBoard = {
      id: boards.length + 1,
      name: 'New Board',
      thumbnailPhoto: 'https://placeholder.com/150'
    }
    setBoards([...boards, newBoard])
  }

  const deleteBoard = (boardId) => {
    // Delete board logic
    const updatedBoards = boards.filter((board) => board.id !== boardId)
    setBoards(updatedBoards)
  }

  return (
    <View style={styles.container}>
      <Toolbar
                hasSelectedImages={selectedImages.length > 0}
                onAdd={() => setIsAddModalOpen(true)} />
      <BoardList navigate={navigate} boards={boards} deleteBoard={deleteBoard} />
      <View style={styles.addBoardButton}>
      </View>
    </View>
  )
}

Main.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func.isRequired
  }).isRequired
}

export default Main
