import data from '../../resources/data.json'
import PropTypes from 'prop-types'
import React, { useState } from 'react'
import styles from './styles'
import { Text, TouchableOpacity, View } from 'react-native'
import Board from '../Board'
import Toolbar from '../ToolBar'
import AddBoardModal from '../AddBoardModal'

const BoardList = ({
  boards,
  navigate,
  deleteBoard
}) => {
  const [boardList, setBoardList] = useState(boards)
  const [selectedBoard, setSelectedBoard] = useState()
  const [boardName, setBoardName] = useState('')
  const [loadingBoard, setLoadingBoard] = useState(true)
  const [isAddModalOpen, setIsAddModalOpen] = useState(false)

  const addBoard = (name, description, thumbnailPhoto) => {
    const id = Math.max(...boardList.map(b => b.id)) + 1
    const updatedBoards = [...boardList, {
      id,
      name,
      description,
      thumbnailPhoto
    }]
    setBoardList(updatedBoards)
    setIsAddModalOpen(false)
    console.log(boards)
  }

  const selectBoard = name => {
    const board = data.boards.find(b => b.name === name)
    setSelectedBoard(board)
    setBoardName(name)
  }

  return (
    <View style={styles.container}>
      <Toolbar
        hasSelected={boardName !== ''}
        onAdd={() => setIsAddModalOpen(true)} />
      <AddBoardModal
        isOpen={isAddModalOpen}
        closeModal={() => setIsAddModalOpen(false)}
        submitModal={addBoard}/>
      <View style={styles.boards}>
        {boardList.map(b => (
          <View key={b.id}>
            <Board
              {...b}
              lists={data.lists}
              boardName={boardName}
              selectBoard={selectBoard}
              navigate={navigate}
            />
            <TouchableOpacity
              style={styles.deleteButton}
              onPress={() => deleteBoard(b.id)}>
              <Text style={styles.deleteButtonText}>Delete Board</Text>
            </TouchableOpacity>
          </View>
        ))}
      </View>
    </View>
  )
}

BoardList.propTypes = {
  boards: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number,
    name: PropTypes.string.isRequired,
    thumbnailPhoto: PropTypes.string
  })).isRequired,
  navigate: PropTypes.func.isRequired,
  deleteBoard: PropTypes.func.isRequired
}

export default BoardList
