import data from '../../resources/data.json'
import PropTypes from 'prop-types'
import React, { useState } from 'react'
import { View } from 'react-native'
import styles from './styles'
import BoardList from '../../components/BoardList'
import Toolbar from '../../components/ToolBar'
import AddBoardModal from '../../components/AddBoardModal'

const Main = ({ navigation: { navigate } }) => {
  const [boards, setBoards] = useState(data.boards)
  const [selectedBoard, setSelectedBoard] = useState([])
  const [loadingBoard, setLoadingBoard] = useState(true)
  const [isAddModalOpen, setIsAddModalOpen] = useState(false)

  const createBoard = () => {
    // Create a new board logic
    const newBoard = {
      id: boards.length + 1,
      name: 'New Board',
      thumbnailPhoto: 'https://placeholder.com/150'
    }
    setBoards([...boards, newBoard])
  }

  const deleteBoard = (boardId) => {
    // Delete board logic
    const updatedBoards = boards.filter((board) => board.id !== boardId)
    setBoards(updatedBoards)
  }

  const onLongPress = name => {
    if (selectedImages.indexOf(name) !== -1) {
        // The image is already within the list
        setSelectedImages(selectedImages.filter(image => image !== name));
    } else {
        // Add the new image
        setSelectedImages([...selectedImages, name]);
    }
}


  return (
    <View style={styles.container}>
      <Toolbar
        hasSelectedBoard={selectedBoard.length > 0}
        onAdd={() => setIsAddModalOpen(true)} />
      <BoardList navigate={navigate} boards={boards} deleteBoard={deleteBoard} onLongPress={onLongPress} />
      <AddBoardModal
        defaultBoard={boards}
        isOpen={isAddModalOpen}
        closeModal={() => setIsAddModalOpen(false)}/>
    </View>
  )
}

Main.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func.isRequired
  }).isRequired
}

export default Main
