import data from '../../resources/data.json'
import PropTypes from 'prop-types'
import React from 'react'
import { View } from 'react-native'
import styles from './styles'
import { BoardList } from '../../components/BoardList'

const Main = ({ navigation: { navigate } }) => {
  const [boards, setBoards] = React.useState(data.boards)

  const createBoard = () => {
    // Create a new board logic
    const newBoard = {
      id: boards.length + 1,
      name: 'New Board',
      thumbnailPhoto: 'https://placeholder.com/150'
    }
    setBoards([...boards, newBoard])
  }

  const deleteBoard = (boardId) => {
    // Delete board logic
    const updatedBoards = boards.filter((board) => board.id !== boardId);
    setBoards(updatedBoards);
  }

  return (
    <View style={styles.container}>
      <BoardList navigate={navigate} boards={boards} deleteBoard={deleteBoard} />
      <TouchableOpacity style={styles.button} onPress={createBoard}>
        <Text style={styles.buttonText}>Create New Board</Text>
      </TouchableOpacity>
    </View>
  );
};


Main.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func.isRequired
  }).isRequired
}


export default Main
