import data from '../../resources/data.json'
import PropTypes from 'prop-types'
import React, { useState } from 'react'
import Task from '../Task'
import { Text, TouchableOpacity, View } from 'react-native'
import styles from './styles'
import Toolbar from '../ToolBar'
import AddTaskModal from '../AddTaskModal'
import AddListModal from '../AddListModal'

const List = ({
  id,
  name,
  color,
  boardId,
  tasks
}) => {
  const [lists, setLists] = useState(data.lists)
  const [selectedList, setSelectedList] = useState([])
  const [listName, setListName] = useState('')
  const [isAddModalOpen, setIsAddModalOpen] = useState(false)
  const [isCreateMode, setIsCreateMode] = useState(true)

  const onEdit = () => {
    if (selectedList) {
      setIsAddModalOpen(true)
      const { id, name, color, boardId } = selectedList
      console.log('id ', id, name, color, boardId)
      editList(id, name, color, boardId)
    }
  }

  const editList = (id, name, color, boardId) => {
    const updatedLists = lists.map((list) =>
      list.id === id ? { ...list, name, color, boardId } : list
    )
    setLists(updatedLists)
    setSelectedList([])
  }

  const deleteList = (listId) => {
    // Delete list logic
    const updatedLists = lists.filter((list) => list.id !== listId)
    setLists(updatedLists)
  }

  const selectList = name => {
    const list = data.lists.find(l => l.name === name)
    setSelectedList(list)
    setListName(name)
  }
  const taskList = tasks ? tasks.filter(t => t.listId === id) : []
  const backgroundStyles = {
    width: 300,
    height: 300,
    padding: 20,
    paddingBottom: 10,
    margin: 10,
    borderWidth: 10,
    borderColor: color,
    borderRadius: 20,
    backgroundColor: 'white'
  }
  const selectedStyle = {
    width: 300,
    height: 300,
    margin: 10,
    borderWidth: 10,
    borderColor: color,
    borderRadius: 20,
    backgroundColor: 'white',
    opacity: 0.5
  }

  const selectTask = name => {
    const task = data.tasks.find(t => t.name === name)
    setSelectedTask(task)
    setTaskName(name)
  }

  return (
    <View style= { listName === name ? selectedStyle : backgroundStyles }>
      <Toolbar
        hasSelected={listName !== ''}
        onAdd={() => setIsAddModalOpen(true)}
        onEdit={() => onEdit()}
        onRemove={() => deleteList()} />
      <AddListModal
        defaultList={lists}
        isOpen={isAddModalOpen}
        closeModal={() => setIsAddModalOpen(false)}
        submitModal={onEdit}/>
      <TouchableOpacity
        onPress={() => listName === name ? selectList('') : selectList(name)}>
        <Text style= { styles.title }>
          { name }
        </Text>
      </TouchableOpacity>
      <Toolbar
        isTaskToolbar={true}
        hasSelected={taskName !== ''}
        onAdd={() => setIsAddModalOpen(true)}
        style={ styles.toolbar }/>
      <AddTaskModal
        defaultTask={tasks}
        isOpen={isAddModalOpen}
        closeModal={() => setIsAddModalOpen(false)}/>
      {taskList.map(t => <Task key={t.id} {...t} selectTask={selectTask} taskName={taskName} />)}
    </View>
  )
}

List.propTypes = {
  id: PropTypes.number,
  name: PropTypes.string.isRequired,
  color: PropTypes.string,
  boardId: PropTypes.number,
  listName: PropTypes.string.isRequired,
  selectList: PropTypes.func.isRequired,
  tasks: PropTypes.arrayOf(PropTypes.shape({
    id: PropTypes.number,
    name: PropTypes.string.isRequired,
    description: PropTypes.string,
    isFinished: PropTypes.string,
    listId: PropTypes.number
  })).isRequired
}

export default List
