import { StyleSheet } from 'react-native';

export default StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'space-around',
  },
  boards: {
    padding: 0,
  },
  deleteButton: {
    marginTop: 0,
    padding: 6,
    backgroundColor: 'red',
    borderRadius: 2,
    width: 120 // Add a fixed width
  },
  deleteButtonText: {
    color: 'white',
    textAlign: 'center',
    alignItems: 'center' // Center the text horizontally
  },
});
